import axios from 'axios';

const url = 'http://localhost:5000/posts';

export const fetchPosts = () => axios.get(url);
export const createPost = (newPost) => {
    try {
        return axios.post(url,newPost);
    }catch (err) {
        return console.log(err);
    }
}
